          >
            Voir d├®tails
            <ArrowRight className="h-4 w-4 ml-1" />
          </Button>
        </div>
      </CardContent>
    </Card>
  );
}

export default CriticalReceivablesAlert;

import React, { useState, useCallback } from 'react';
import { useLocale } from '@/contexts/LocaleContext';
import { FileUp, FileText, AlertCircle, Info } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Alert, AlertDescription, AlertTitle } from '@/components/ui/alert';
import { Card, CardContent } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import type { ParseResult } from '@/utils/accountingFileParser';

interface FECImportDropzoneProps {
  onFileSelected: (file: File) => void;
  isProcessing: boolean;
  parseResult?: ParseResult | null;
}

const FECImportDropzone: React.FC<FECImportDropzoneProps> = ({ onFileSelected, isProcessing, parseResult }) => {
  const { t } = useLocale();
  const [dragActive, setDragActive] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const handleDrag = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === 'dragenter' || e.type === 'dragover') {
      setDragActive(true);
    } else if (e.type === 'dragleave') {
      setDragActive(false);
    }
  }, []);

  const handleDrop = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);

    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      validateAndProcessFile(e.dataTransfer.files[0]);
    }
  }, []);

  const handleFileChange = useCallback((e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files[0]) {
      validateAndProcessFile(e.target.files[0]);
    }
  }, []);

  const validateAndProcessFile = (file: File) => {
    setError(null);

    // Accepter tous les formats de fichiers comptables
    const validExtensions = /\.(txt|csv|tsv|dat|fec|iif|xlsx?)$/i;
    if (!file.name.match(validExtensions) && file.name.indexOf('.') !== -1) {
      setError(t('fecImport.error.fileType', {
        defaultValue: 'Format de fichier non support├®. Formats accept├®s: .txt, .csv, .tsv, .dat, .fec, .iif, .xls, .xlsx'
      }));
      return;
    }

    // Validate file size (max 50MB pour supporter de gros fichiers)
    if (file.size > 50 * 1024 * 1024) {
      setError(t('fecImport.error.fileSize', { defaultValue: 'Fichier trop volumineux. Taille maximale: 50MB.' }));
      return;
    }

    onFileSelected(file);
  };

  const formatNumber = (num: number): string => {
    return new Intl.NumberFormat('fr-FR', { minimumFractionDigits: 2, maximumFractionDigits: 2 }).format(num);
  };

  const getFormatBadgeColor = (format: string): string => {
    switch (format) {
      case 'FEC': return 'bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200';
      case 'SYSCOHADA': return 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200';
      case 'IFRS_CSV': return 'bg-purple-100 text-purple-800 dark:bg-purple-900 dark:text-purple-200';
      case 'SCF': return 'bg-orange-100 text-orange-800 dark:bg-orange-900 dark:text-orange-200';
      case 'QUICKBOOKS': return 'bg-indigo-100 text-indigo-800 dark:bg-indigo-900 dark:text-indigo-200';
      case 'SAGE': return 'bg-pink-100 text-pink-800 dark:bg-pink-900 dark:text-pink-200';
      case 'XERO': return 'bg-cyan-100 text-cyan-800 dark:bg-cyan-900 dark:text-cyan-200';
      default: return 'bg-gray-100 text-gray-800 dark:bg-gray-900 dark:text-gray-200';
    }
  };

  return (
    <div className="w-full space-y-4">
      {error && (
        <Alert variant="destructive">
          <AlertCircle className="h-4 w-4" />
          <AlertTitle>{t('error')}</AlertTitle>
          <AlertDescription>{error}</AlertDescription>
        </Alert>
      )}

      {parseResult && parseResult.warnings.length > 0 && (
        <Alert>
          <Info className="h-4 w-4" />
          <AlertTitle>Informations de d├®tection</AlertTitle>
          <AlertDescription>
            <ul className="list-disc list-inside space-y-1 mt-2">
              {parseResult.warnings.map((warning, idx) => (
                <li key={idx} className="text-sm">{warning}</li>
              ))}
            </ul>
          </AlertDescription>
        </Alert>
      )}

      {parseResult && parseResult.success && (
        <div className="space-y-4">
          {/* Infos d├®tect├®es */}
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
            <Card>
              <CardContent className="pt-4">
                <div className="text-sm text-muted-foreground">Format</div>
                <Badge className={`mt-1 ${getFormatBadgeColor(parseResult.format)}`}>
                  {parseResult.format}
                </Badge>
              </CardContent>
            </Card>
            <Card>
              <CardContent className="pt-4">
                <div className="text-sm text-muted-foreground">Standard</div>
                <div className="font-semibold">{parseResult.standard || 'Non d├®tect├®'}</div>
              </CardContent>
            </Card>
            <Card>
              <CardContent className="pt-4">
                <div className="text-sm text-muted-foreground">Devises</div>
                <div className="font-semibold">{parseResult.stats.currencies.join(', ')}</div>
              </CardContent>
            </Card>
            <Card>
              <CardContent className="pt-4">
                <div className="text-sm text-muted-foreground">P├®riode</div>
                <div className="text-xs font-semibold">
                  {parseResult.stats.dateRange
                    ? `${parseResult.stats.dateRange.start} ÔåÆ ${parseResult.stats.dateRange.end}`
                    : '-'
                  }
                </div>
              </CardContent>
            </Card>
          </div>

          {/* Statistiques */}
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
            <div className="text-center p-4 bg-green-50 dark:bg-green-950 rounded-lg border border-green-200 dark:border-green-800">
              <div className="text-2xl font-bold text-green-600 dark:text-green-400">{parseResult.stats.validLines}</div>
              <div className="text-sm text-green-700 dark:text-green-300">Lignes valides</div>
            </div>
            <div className="text-center p-4 bg-blue-50 dark:bg-blue-950 rounded-lg border border-blue-200 dark:border-blue-800">
              <div className="text-xl font-bold text-blue-600 dark:text-blue-400">{formatNumber(parseResult.stats.totalDebit)}</div>
              <div className="text-sm text-blue-700 dark:text-blue-300">Total D├®bit</div>
            </div>
            <div className="text-center p-4 bg-purple-50 dark:bg-purple-950 rounded-lg border border-purple-200 dark:border-purple-800">
              <div className="text-xl font-bold text-purple-600 dark:text-purple-400">{formatNumber(parseResult.stats.totalCredit)}</div>
              <div className="text-sm text-purple-700 dark:text-purple-300">Total Cr├®dit</div>
            </div>
            <div className="text-center p-4 bg-amber-50 dark:bg-amber-950 rounded-lg border border-amber-200 dark:border-amber-800">
              <div className="text-xl font-bold text-amber-600 dark:text-amber-400">{formatNumber(Math.abs(parseResult.stats.balance))}</div>
              <div className="text-sm text-amber-700 dark:text-amber-300">
                {Math.abs(parseResult.stats.balance) < 0.01 ? 'Ô£ô ├ëquilibr├®' : 'ÔÜá ├ëcart'}
              </div>
            </div>
          </div>

          {/* Journaux d├®tect├®s */}
          {parseResult.stats.journals.length > 0 && (
            <Card>
              <CardContent className="pt-4">
                <div className="text-sm font-medium mb-2">Journaux d├®tect├®s ({parseResult.stats.journals.length})</div>
                <div className="flex flex-wrap gap-2">
                  {parseResult.stats.journals.map((journal, idx) => (
                    <Badge key={idx} variant="outline">{journal}</Badge>
                  ))}
                </div>
              </CardContent>
            </Card>
          )}

          {/* Erreurs */}
          {parseResult.errors.length > 0 && (
            <Alert variant="destructive">
              <AlertCircle className="h-4 w-4" />
              <AlertTitle>Erreurs d├®tect├®es ({parseResult.errors.length})</AlertTitle>
              <AlertDescription>
                <ul className="list-disc list-inside space-y-1 mt-2 max-h-40 overflow-y-auto">
                  {parseResult.errors.slice(0, 10).map((err, idx) => (
                    <li key={idx} className="text-sm">
                      Ligne {err.line}: {err.message}
                    </li>
                  ))}
                  {parseResult.errors.length > 10 && (
                    <li className="text-sm font-semibold">... et {parseResult.errors.length - 10} autres erreurs</li>
                  )}
                </ul>
              </AlertDescription>
            </Alert>
          )}
        </div>
      )}

      <div
        className={`flex flex-col items-center justify-center w-full h-64 border-2 border-dashed rounded-lg p-6 transition-colors ${
          dragActive ? 'border-blue-600 bg-blue-600/5' : 'border-gray-300 dark:border-gray-600'
        }`}
        onDragEnter={handleDrag}
        onDragLeave={handleDrag}
        onDragOver={handleDrag}
        onDrop={handleDrop}
      >
        <FileUp className="h-16 w-16 text-gray-400 dark:text-gray-500 mb-4" />
        <p className="mb-2 text-sm text-gray-500 dark:text-gray-300">
          <span className="font-semibold">{t('fecImport.dropzone.clickToUpload', { defaultValue: 'Cliquer pour importer' })}</span> {t('fecImport.dropzone.orDragAndDrop', { defaultValue: 'ou glisser-d├®poser' })}
        </p>
        <p className="text-xs text-gray-500 dark:text-gray-300 text-center">
          {t('fecImport.dropzone.fileTypes', {
            defaultValue: 'Formats support├®s: FEC, SYSCOHADA, IFRS, SCF, QuickBooks, Sage, Xero'
          })}
        </p>
        <p className="text-xs text-gray-400 dark:text-gray-400 text-center mt-1">
          .txt, .csv, .tsv, .dat, .fec, .iif, .xls, .xlsx
        </p>
        <input
          id="dropzone-file"
          type="file"
          className="hidden"
          aria-label="S├®lectionner un fichier FEC ├á importer"
          accept=".txt,.csv,.tsv,.dat,.fec,.iif,.xls,.xlsx"
          onChange={handleFileChange}
          disabled={isProcessing}
        >
        <Button
          variant="outline"
          className="mt-4"
          onClick={() => document.getElementById('dropzone-file')?.click()}
          disabled={isProcessing}
        >
          <FileText className="mr-2 h-4 w-4" />
          {t('fecImport.dropzone.selectFile', { defaultValue: 'S├®lectionner un fichier' })}
        </Button>
      </div>
    </div>
  );
};

export default FECImportDropzone;

import React, { useState, useCallback } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { CurrencyAmount } from '@/components/ui/CurrencyAmount';
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Progress } from '@/components/ui/progress';
import { Badge } from '@/components/ui/badge';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { useToast } from '@/components/ui/use-toast';
import {
  Upload,
  FileArchive,
  CheckCircle,
  AlertTriangle,
  FileText,
  Trash2,
  Eye,
  RefreshCw,
  Database
} from 'lucide-react';
import { fecService } from '@/services/fecService';

// Types
interface FECAnalysisResult {
  company: { name: string; siren?: string; period?: { start: string; end: string } };
  statistics: {
    totalEntries: number;
    totalAccounts: number;
    totalJournals: number;
    dateRange: { start: string; end: string };
    totalDebit?: number;
    totalCredit?: number;
    unbalancedEntries?: number;
  };
  warnings: Array<string | { line?: number; message: string }>;
  preview?: {
    accounts?: any[];
    entries?: any[];
    sampleEntries?: any[];
  };
}

interface FECImportResult {
  success: boolean;
  accountsCreated: number;
  journalsCreated: number;
  entriesCreated: number;
  errors: string[];
}

export default function FECImportTab() {
  const { toast } = useToast();

  // ├ëtats
  const [isDragOver, setIsDragOver] = useState(false);
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [isProcessing, setIsProcessing] = useState(false);
  const [processingStep, setProcessingStep] = useState('');
  const [progress, setProgress] = useState(0);
  const [analysisResult, setAnalysisResult] = useState<FECAnalysisResult | null>(null);
  const [importResult, setImportResult] = useState<FECImportResult | null>(null);
  const [showPreview, setShowPreview] = useState(false);



  // Simulation des ├®tapes de traitement

  const processingSteps = [

    { key: 'reading', label: 'Lecture du fichier...', duration: 1000 },

    { key: 'parsing', label: 'Analyse du format FEC...', duration: 2000 },

    { key: 'validation', label: 'Validation des donn├®es...', duration: 1500 },

    { key: 'preview', label: 'G├®n├®ration de l\'aper├ºu...', duration: 800 }

  ];



  const importSteps = [

    { key: 'accounts', label: 'Cr├®ation des comptes...', duration: 1200 },

    { key: 'journals', label: 'Cr├®ation des journaux...', duration: 800 },

    { key: 'entries', label: 'Import des ├®critures...', duration: 2500 },

    { key: 'balance', label: 'V├®rification des ├®quilibres...', duration: 1000 }

  ];



  // Ô£à Analyse FEC r├®elle via fecService



  // Gestion du drag & drop
  const handleDragOver = useCallback((e: React.DragEvent<HTMLDivElement>) => {
    e.preventDefault();
    setIsDragOver(true);
  }, []);

  const handleDragLeave = useCallback((e: React.DragEvent<HTMLDivElement>) => {
    e.preventDefault();
    setIsDragOver(false);

  }, []);



  const handleDrop = useCallback((e: React.DragEvent<HTMLDivElement>) => {
    e.preventDefault();
    setIsDragOver(false);

    const files = Array.from(e.dataTransfer.files);
    handleFileSelection(files[0]);
  }, []);

  const handleFileSelection = (file: File | undefined) => {
    if (!file) return;



    // Validation du type de fichier

    const validExtensions = ['.txt', '.csv'];

    const fileExtension = file.name.toLowerCase().substring(file.name.lastIndexOf('.'));



    if (!validExtensions.includes(fileExtension) && file.name.includes('.')) {

      toast({

        variant: "destructive",

        title: "Fichier non valide",

        description: "Veuillez s├®lectionner un fichier FEC (.txt ou .csv)"

      });

      return;

    }



    // Validation de la taille (max 10MB)

    if (file.size > 10 * 1024 * 1024) {

      toast({

        variant: "destructive",

        title: "Fichier trop volumineux",

        description: "La taille maximale autoris├®e est de 10 MB"

      });

      return;

    }



    setSelectedFile(file);

    setAnalysisResult(null);

    setImportResult(null);

  };



  const handleFileInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    handleFileSelection(file);
  };



  const processFile = async () => {

    if (!selectedFile) return;



    setIsProcessing(true);

    setProgress(0);



    try {

      // Simulation du traitement

      for (let i = 0; i < processingSteps.length; i++) {

        const step = processingSteps[i];

        setProcessingStep(step.label);



        await new Promise(resolve => setTimeout(resolve, step.duration));

        setProgress(((i + 1) / processingSteps.length) * 100);

      }



      // Simulation d'analyse r├®ussie

      // Ô£à Analyser le fichier FEC avec le vrai service
        const analysis = await fecService.analyzeFECFile(selectedFile);
        setAnalysisResult({
          company: { name: '', siren: '', period: { start: '', end: '' } },
          statistics: {
            totalEntries: analysis.totalEntries || 0,
            totalAccounts: analysis.accountCount || 0,
            totalJournals: analysis.journalCount || 0,
            dateRange: { start: '', end: '' },
            totalDebit: analysis.totalDebit || 0,
            totalCredit: analysis.totalCredit || 0,
            unbalancedEntries: 0
          },
          warnings: analysis.warnings || [],
          preview: {
            accounts: [],
            entries: [],
            sampleEntries: []
          }
        });

        if (!analysis.isValid) {
          toast({
            title: 'Fichier FEC invalide',
            description: ` erreur(s) d├®tect├®e(s)`,
            variant: 'destructive'
          });
        }



      toast({

        title: "Analyse termin├®e",

        description: "Le fichier FEC a ├®t├® analys├® avec succ├¿s",

        action: <CheckCircle className="text-green-500" />

      });



    } catch (_error) {

      toast({

        variant: "destructive",

        title: "Erreur d'analyse",

        description: "Une erreur est survenue lors de l'analyse du fichier"

      });

    } finally {

      setIsProcessing(false);

      setProcessingStep('');

      setProgress(0);

    }

  };



  const importData = async () => {

    if (!analysisResult) return;



    setIsProcessing(true);

    setProgress(0);



    try {

      // Simulation de l'import

      for (let i = 0; i < importSteps.length; i++) {

        const step = importSteps[i];

        setProcessingStep(step.label);



        await new Promise(resolve => setTimeout(resolve, step.duration));

        setProgress(((i + 1) / importSteps.length) * 100);

      }



      // R├®sultat de l'import

      const result = {

        success: true,

        accountsCreated: analysisResult.statistics.totalAccounts,

        journalsCreated: analysisResult.statistics.totalJournals,

        entriesCreated: analysisResult.statistics.totalEntries,

        errors: []

      };



      setImportResult(result);



      toast({

        title: "Import termin├®",

        description: `${result.entriesCreated} ├®critures import├®es avec succ├¿s`,

        action: <CheckCircle className="text-green-500" />

      });



    } catch (_error) {

      toast({

        variant: "destructive",

        title: "Erreur d'import",

        description: "Une erreur est survenue lors de l'import des donn├®es"

      });

    } finally {

      setIsProcessing(false);

      setProcessingStep('');

      setProgress(0);

    }

  };



  const resetImport = () => {

    setSelectedFile(null);

    setAnalysisResult(null);

    setImportResult(null);

    setShowPreview(false);

  };



  return (

    <div className="space-y-6">

      {/* Header */}

      <Card>

        <CardHeader>

          <CardTitle className="flex items-center gap-2">

            <FileArchive className="h-5 w-5" />

            Import FEC (Fichier des ├ëcritures Comptables)

          </CardTitle>

          <CardDescription>

            Importez vos donn├®es comptables ├á partir d'un fichier FEC standard

          </CardDescription>

        </CardHeader>

      </Card>



      {/* Zone de drop ou r├®sultats */}

      {!selectedFile && !importResult && (

        <Card>

          <CardContent className="p-12">

            <div

              className={`border-2 border-dashed rounded-lg p-12 text-center transition-all duration-200 ${

                isDragOver 

                  ? 'border-primary bg-primary/5' 

                  : 'border-gray-300 hover:border-gray-400'

              }`}

              onDragOver={handleDragOver}

              onDragLeave={handleDragLeave}

              onDrop={handleDrop}

            >

              <motion.div

                initial={{ scale: 0.9, opacity: 0 }}

                animate={{ scale: 1, opacity: 1 }}

                className="space-y-4"

              >

                <div className="mx-auto w-16 h-16 bg-primary/10 rounded-full flex items-center justify-center">

                  <Upload className="h-8 w-8 text-primary" />

                </div>



                <div className="space-y-2">

                  <h3 className="text-lg font-semibold">

                    Glissez-d├®posez votre fichier FEC ici

                  </h3>

                  <p className="text-muted-foreground">

                    ou cliquez pour s├®lectionner un fichier

                  </p>

                </div>



                <div className="space-y-2">

                  <Button

                    onClick={() => document.getElementById('file-input')?.click()}

                    className="bg-primary hover:bg-primary/90"

                  >

                    <FileText className="mr-2 h-4 w-4" />

                    S├®lectionner un fichier

                  </Button>

                  <input aria-label="Input" placeholder=""

                    id="file-input"

                    type="file"

                    accept=".txt,.csv"

                    onChange={handleFileInputChange}

                    className="hidden"

                  >

                </div>



                <div className="text-xs text-muted-foreground space-y-1">

                  <p>Formats accept├®s : .txt, .csv</p>

                  <p>Taille maximale : 10 MB</p>

                  <p>Format FEC standard requis</p>

                </div>

              </motion.div>

            </div>

          </CardContent>

        </Card>

      )}



      {/* Fichier s├®lectionn├® */}

      {selectedFile && !analysisResult && !isProcessing && (

        <Card>

          <CardContent className="p-6">

            <div className="flex items-center justify-between">

              <div className="flex items-center gap-4">

                <div className="p-3 bg-blue-100 rounded-lg">

                  <FileText className="h-6 w-6 text-blue-600" />

                </div>

                <div>

                  <h3 className="font-semibold">{selectedFile.name}</h3>

                  <p className="text-sm text-muted-foreground">

                    {(selectedFile.size / 1024 / 1024).toFixed(2)} MB

                  </p>

                </div>

              </div>



              <div className="flex gap-2">

                <Button variant="outline" onClick={resetImport}>

                  <Trash2 className="mr-2 h-4 w-4" />

                  Supprimer

                </Button>

                <Button onClick={processFile}>

                  <Eye className="mr-2 h-4 w-4" />

                  Analyser le fichier

                </Button>

              </div>

            </div>

          </CardContent>

        </Card>

      )}



      {/* Traitement en cours */}

      {isProcessing && (

        <Card>

          <CardContent className="p-8">

            <div className="text-center space-y-4">

              <div className="animate-spin mx-auto w-12 h-12 border-4 border-primary border-t-transparent rounded-full"></div>

              <div>

                <h3 className="text-lg font-semibold mb-2">{processingStep}</h3>

