                    <ul className="text-sm space-y-1">

                      {analysisResult.warnings.slice(0, 3).map((warning, index) => {
                        const w = typeof warning === 'string' ? { message: warning } : warning;
                        return (
                          <li key={index}>
                            {w.line ? `Ligne ${w.line}: ` : ''}{w.message}
                          </li>
                        );
                      })}

                      {analysisResult.warnings.length > 3 && (

                        <li className="text-muted-foreground">

                          ... et {analysisResult.warnings.length - 3} autres

                        </li>

                      )}

                    </ul>

                  </AlertDescription>

                </Alert>

              )}



              <div className="flex justify-between items-center mt-6">

                <div className="flex gap-2">

                  <Button variant="outline" onClick={() => setShowPreview(!showPreview)}>

                    <Eye className="mr-2 h-4 w-4" />

                    {showPreview ? 'Masquer' : 'Aper├ºu'} des donn├®es

                  </Button>

                  <Button variant="outline" onClick={resetImport}>

                    <RefreshCw className="mr-2 h-4 w-4" />

                    Recommencer

                  </Button>

                </div>

                <Button onClick={importData} className="bg-green-600 hover:bg-green-700">

                  <Database className="mr-2 h-4 w-4" />

                  Importer les donn├®es

                </Button>

              </div>

            </CardContent>

          </Card>



          {/* Aper├ºu des donn├®es */}

          <AnimatePresence>

            {showPreview && (

              <motion.div

                initial={{ opacity: 0, height: 0 }}

                animate={{ opacity: 1, height: 'auto' }}

                exit={{ opacity: 0, height: 0 }}

                className="space-y-4"

              >

                {/* Aper├ºu des comptes */}

                <Card>

                  <CardHeader>

                    <CardTitle>Aper├ºu des comptes ({analysisResult.preview?.accounts?.length ?? 0})</CardTitle>

                  </CardHeader>

                  <CardContent>

                    <Table>

                      <TableHeader>

                        <TableRow>

                          <TableHead>Code</TableHead>

