                      <TableBody>

                        {(analysisResult.preview?.accounts ?? []).map((account, index) => (

                          <TableRow key={index}>

                            <TableCell className="font-mono">{account.code}</TableCell>

                            <TableCell>{account.name}</TableCell>

                            <TableCell>

