import React, { useState, useEffect, useMemo, useCallback } from 'react';
import { CurrencyAmount } from '@/components/ui/CurrencyAmount';
import { motion } from 'framer-motion';
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Badge } from '@/components/ui/badge';
import { Progress } from '@/components/ui/progress';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { useToast } from '@/components/ui/use-toast';
import { logger } from '@/lib/logger';
import { supabase } from '@/lib/supabase';
import bankReconciliationService, {
  ReconciliationMatch,
  ReconciliationSummary,
  AccountingEntry
} from '@/services/bankReconciliationService';
import {
  Shuffle,
  CheckCircle,
  XCircle,
  AlertCircle,
  Search,
  Calendar,
  Loader2,
  BarChart3,
  Target,
  Eye,
  Edit3,
  RefreshCw,
  Link,
  Clock,
  FileText,
  Database,
  Zap
} from 'lucide-react';

// Types pour les transactions et entr├®es
type BankTransaction = {
  id: string;
  date: string;
  transaction_date?: string;
  amount: number;
  description: string;
  reference: string;
  is_reconciled: boolean;
  suggested_matches?: string[];
  matched_entry_id?: string;
  company_id?: string;
  bank_account_id?: string;
};
interface BankReconciliationProps {
  currentEnterprise: { id: string; name?: string } | null;
  bankAccounts: Array<{ id: string; bank_name: string; account_name: string }>;
  onReconciliationComplete?: (summary?: ReconciliationSummary) => void;
}
const BankReconciliation = ({ currentEnterprise, bankAccounts, onReconciliationComplete }: BankReconciliationProps) => {
  const { toast } = useToast();

  // ├ëtats
  const [selectedAccount, setSelectedAccount] = useState('');
  const [selectedPeriod, setSelectedPeriod] = useState('month');
  const [isReconciling, setIsReconciling] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [autoMatches, setAutoMatches] = useState<ReconciliationMatch[]>([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterStatus, setFilterStatus] = useState('all');
  const [showDetails, setShowDetails] = useState(false);
  const [reconciliationSummary, setReconciliationSummary] = useState<ReconciliationSummary | null>(null);
  const [reconciliationInProgress, setReconciliationInProgress] = useState<{ [key: string]: boolean }>({});
  const [reconciledTransactions, setReconciledTransactions] = useState<Set<string>>(new Set());

  // ├ëtats pour la r├®conciliation manuelle
  const [selectedManualTransaction, setSelectedManualTransaction] = useState<string | null>(null);
  const [selectedManualEntry, setSelectedManualEntry] = useState<string | null>(null);

  // Donn├®es r├®elles depuis Supabase
  const [bankTransactions, setBankTransactions] = useState<BankTransaction[]>([]);
  const [accountingEntries, setAccountingEntries] = useState<Array<AccountingEntry & { account?: string }>>([]);

  // Calcul des dates de p├®riode
  const getPeriodDates = useCallback(() => {
    const now = new Date();
    let startDate: string;
    const endDate = now.toISOString().split('T')[0];

    switch (selectedPeriod) {
      case 'week':
        const weekAgo = new Date(now);
        weekAgo.setDate(weekAgo.getDate() - 7);
        startDate = weekAgo.toISOString().split('T')[0];
        break;
      case 'month':
        const monthAgo = new Date(now);
        monthAgo.setMonth(monthAgo.getMonth() - 1);
        startDate = monthAgo.toISOString().split('T')[0];
        break;
      case 'quarter':
        const quarterAgo = new Date(now);
        quarterAgo.setMonth(quarterAgo.getMonth() - 3);
        startDate = quarterAgo.toISOString().split('T')[0];
        break;
      case 'year':
        const yearAgo = new Date(now);
        yearAgo.setFullYear(yearAgo.getFullYear() - 1);
        startDate = yearAgo.toISOString().split('T')[0];
        break;
      default:
        const defaultMonth = new Date(now);
        defaultMonth.setMonth(defaultMonth.getMonth() - 1);
        startDate = defaultMonth.toISOString().split('T')[0];
    }

    return { startDate, endDate };
  }, [selectedPeriod]);

  // Charger les transactions bancaires depuis Supabase
  const loadBankTransactions = useCallback(async () => {
    if (!currentEnterprise?.id || !selectedAccount) return;

    setIsLoading(true);
    try {
      const { startDate, endDate } = getPeriodDates();

      const { data, error } = await supabase
        .from('bank_transactions')
        .select('*')
        .eq('company_id', currentEnterprise.id)
        .eq('bank_account_id', selectedAccount)
        .gte('transaction_date', startDate)
        .lte('transaction_date', endDate)
        .order('transaction_date', { ascending: false });

      if (error) throw error;

      const formattedTransactions: BankTransaction[] = (data || []).map(tx => ({
        id: tx.id,
        date: tx.transaction_date || tx.date,
        transaction_date: tx.transaction_date,
        amount: tx.amount,
        description: tx.description || '',
        reference: tx.reference || '',
        is_reconciled: tx.is_reconciled || false,
        matched_entry_id: tx.matched_entry_id,
        company_id: tx.company_id,
        bank_account_id: tx.bank_account_id
      }));

      setBankTransactions(formattedTransactions);
      logger.info('BankReconciliation', `${formattedTransactions.length} transactions charg├®es`);
    } catch (error) {
      logger.error('BankReconciliation', 'Erreur chargement transactions:', error);
      toast({
        title: "Erreur",
        description: "Impossible de charger les transactions bancaires",
        variant: "destructive"
      });
    } finally {
      setIsLoading(false);
    }
  }, [currentEnterprise?.id, selectedAccount, getPeriodDates, toast]);

  // Charger les ├®critures comptables depuis Supabase
  const loadAccountingEntries = useCallback(async () => {
    if (!currentEnterprise?.id) return;

    try {
      const { startDate, endDate } = getPeriodDates();

      const { data, error } = await supabase
        .from('journal_entry_lines')
        .select(`
          id,
          debit,
          credit,
          description,
          account:accounts(account_number, name),
          journal_entry:journal_entries(
            id,
            entry_date,
            description,
            entry_number,
            company_id
          )
        `)
        .gte('journal_entry.entry_date', startDate)
        .lte('journal_entry.entry_date', endDate)
        .eq('journal_entry.company_id', currentEnterprise.id);

      if (error) throw error;

      const formattedEntries = (data || [])
        .filter(line => line.journal_entry)
        .map(line => ({
          id: line.id,
          company_id: (line.journal_entry as any)?.company_id || currentEnterprise.id,
          account_number: (line.account as any)?.account_number || '',
          date: (line.journal_entry as any)?.entry_date || '',
          amount: (line.debit || 0) - (line.credit || 0),
          debit: line.debit || 0,
          credit: line.credit || 0,
          description: line.description || (line.journal_entry as any)?.description || '',
          reference: (line.journal_entry as any)?.entry_number || '',
          journal_entry_id: (line.journal_entry as any)?.id,
          reconciled: false,
          account: `${(line.account as any)?.account_number || ''} - ${(line.account as any)?.name || ''}`,
          created_at: '',
          updated_at: ''
        }));

      setAccountingEntries(formattedEntries);
      logger.info('BankReconciliation', `${formattedEntries.length} ├®critures charg├®es`);
    } catch (error) {
      logger.error('BankReconciliation', 'Erreur chargement ├®critures:', error);
    }
  }, [currentEnterprise?.id, getPeriodDates]);

  // Charger les donn├®es quand le compte ou la p├®riode change
  useEffect(() => {
    if (selectedAccount && currentEnterprise?.id) {
      loadBankTransactions();
      loadAccountingEntries();
    }
  }, [selectedAccount, selectedPeriod, currentEnterprise?.id, loadBankTransactions, loadAccountingEntries]);
  // Calculs des statistiques
  const reconciliationStats = useMemo(() => {
    const totalBankTransactions = bankTransactions.length;
    const reconciledTransactions = bankTransactions.filter(t => t.is_reconciled).length;
    const pendingTransactions = totalBankTransactions - reconciledTransactions;
    const reconciliationRate = totalBankTransactions > 0 ? (reconciledTransactions / totalBankTransactions) * 100 : 0;
    const totalAmount = bankTransactions.reduce((sum, t) => sum + Math.abs(t.amount), 0);
    const reconciledAmount = bankTransactions
      .filter(t => t.is_reconciled)
      .reduce((sum, t) => sum + Math.abs(t.amount), 0);
    const pendingAmount = totalAmount - reconciledAmount;
    return {
      totalBankTransactions,
      reconciledTransactions,
      pendingTransactions,
      reconciliationRate,
      totalAmount,
      reconciledAmount,
      pendingAmount
    };
  }, [bankTransactions]);
  const markTransactionAsReconciled = (transactionId: string, matchedEntryId?: string) => {
    setReconciledTransactions(prev => new Set([...prev, transactionId]));
    setBankTransactions(prev => prev.map(tx =>
      tx.id === transactionId
        ? { ...tx, is_reconciled: true, matched_entry_id: matchedEntryId ?? tx.matched_entry_id }
        : tx
    ));
  };
  const sendToCategorization = async (transactionId: string) => {
    try {
      // Annuler la r├®conciliation via le service
      const success = await bankReconciliationService.cancelReconciliation(transactionId);

      if (success) {
        // Mise ├á jour locale
        setReconciledTransactions(prev => {
          const clone = new Set([...prev]);
          clone.delete(transactionId);
          return clone;
        });
        setBankTransactions(prev => prev.map(tx =>
          tx.id === transactionId
            ? { ...tx, is_reconciled: false, matched_entry_id: undefined }
            : tx
        ));

        toast({
          title: "R├®conciliation annul├®e",
          description: "La transaction peut ├¬tre re-cat├®goris├®e",
        });
      } else {
        throw new Error('├ëchec de l\'annulation');
      }
    } catch (error) {
      logger.error('BankReconciliation', 'Erreur annulation r├®conciliation:', error);
      toast({
        title: "Erreur",
        description: "Impossible d'annuler la r├®conciliation",
        variant: "destructive"
      });
    }
  };
  // Filtrage des transactions
  const filteredTransactions = useMemo(() => {
    let filtered = bankTransactions;
    if (searchTerm) {
      filtered = filtered.filter(t => 
        t.description.toLowerCase().includes(searchTerm.toLowerCase()) ||
        t.reference.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }
    if (filterStatus !== 'all') {
      filtered = filtered.filter(t => {
        switch (filterStatus) {
          case 'reconciled': return t.is_reconciled;
          case 'pending': return !t.is_reconciled;
          case 'suggested': return !t.is_reconciled && t.suggested_matches && t.suggested_matches.length > 0;
          default: return true;
        }
      });
    }
    return filtered;
  }, [bankTransactions, searchTerm, filterStatus]);
  // Lancement de la r├®conciliation automatique
  const runAutoReconciliation = async () => {
    if (!selectedAccount || !currentEnterprise?.id) {
      toast({
        title: "Compte requis",
        description: "Veuillez s├®lectionner un compte bancaire",
        variant: "destructive"
      });
      return;
    }

    setIsReconciling(true);
    try {
      const { startDate, endDate } = getPeriodDates();

      // Appel au service de r├®conciliation r├®el
      const matches = await bankReconciliationService.runAutoReconciliation(
        currentEnterprise.id,
        selectedAccount,
        startDate,
        endDate
      );

      setAutoMatches(matches);

      // Recharger les transactions pour refl├®ter les r├®conciliations automatiques
      await loadBankTransactions();

      toast({
        title: "R├®conciliation automatique termin├®e",
        description: `${matches.length} correspondance(s) trouv├®e(s)`,
        variant: "default"
      });
    } catch (error) {
      logger.error('BankReconciliation', 'Erreur lors de la r├®conciliation automatique:', error instanceof Error ? error.message : String(error));
      toast({
        title: "Erreur",
        description: "├ëchec de la r├®conciliation automatique",
        variant: "destructive"
      });
    } finally {
      setIsReconciling(false);
    }
  };
  // Validation d'une correspondance
  const validateMatch = async (bankTransactionId: string, accountingEntryId: string) => {
    try {
      setReconciliationInProgress(prev => ({ ...prev, [`${bankTransactionId}-${accountingEntryId}`]: true }));

      // Appel au service de r├®conciliation r├®el
      const success = await bankReconciliationService.validateReconciliation(
        bankTransactionId,
        accountingEntryId
      );

      if (success) {
        // Marquer la transaction comme r├®concili├®e localement
        markTransactionAsReconciled(bankTransactionId, accountingEntryId);

        toast({
          title: "Ô£à Correspondance valid├®e",
          description: "La r├®conciliation a ├®t├® enregistr├®e",
          variant: "default"
        });

        // Retirer de la liste des matches automatiques
        setAutoMatches(prev => prev.filter(m =>
          m.bank_transaction.id !== bankTransactionId
        ));
      } else {
        throw new Error('├ëchec de la validation');
      }
    } catch (error) {
      logger.error('BankReconciliation', 'Erreur lors de la validation:', error instanceof Error ? error.message : String(error));
      toast({
        title: "ÔØî Erreur",
        description: "├ëchec de la r├®conciliation",
        variant: "destructive"
      });
    } finally {
      setReconciliationInProgress(prev => {
        const newState = { ...prev };
        delete newState[`${bankTransactionId}-${accountingEntryId}`];
        return newState;
      });
    }
  };
  // R├®concilier une transaction sp├®cifique (g├®n├¿re une ├®criture si n├®cessaire)
  const reconcileTransaction = async (transactionId: string) => {
    try {
      setReconciliationInProgress(prev => ({ ...prev, [transactionId]: true }));

      const transaction = bankTransactions.find(t => t.id === transactionId);
      if (!transaction) {
        throw new Error('Transaction non trouv├®e');
      }

      // G├®n├®rer une ├®criture comptable pour la transaction non r├®concili├®e
      const result = await bankReconciliationService.generateEntryForUnmatchedTransaction(
        transaction as any
      );

      if (result.success) {
