                      <div className="text-center py-8 text-gray-500">
                        <CheckCircle className="h-12 w-12 mx-auto mb-3 text-green-500" />
                        <p>Toutes les ├®critures sont lettr├®es !</p>
                      </div>
                    )}
                  </div>
                </CardContent>
              </Card>
            </div>

            {/* Boutons d'action */}
            {(selectedManualTransaction || selectedManualEntry) && (
              <motion.div
                initial={{ opacity: 0, y: 10 }}
                animate={{ opacity: 1, y: 0 }}
                className="flex justify-center gap-4"
              >
                <Button
                  variant="outline"
                  onClick={() => {
                    setSelectedManualTransaction(null);
                    setSelectedManualEntry(null);
                  }}
                >
                  <XCircle className="h-4 w-4 mr-2" />
                  Annuler la s├®lection
                </Button>
                <Button
                  disabled={!selectedManualTransaction || !selectedManualEntry}
                  onClick={() => {
                    if (selectedManualTransaction && selectedManualEntry) {
                      markTransactionAsReconciled(selectedManualTransaction, selectedManualEntry);
                      toast({
                        title: "R├®conciliation effectu├®e",
                        description: "La transaction a ├®t├® li├®e ├á l'├®criture comptable.",
                      });
                      setSelectedManualTransaction(null);
                      setSelectedManualEntry(null);
                    }
                  }}
                  className="bg-gradient-to-r from-green-600 to-emerald-600 hover:from-green-700 hover:to-emerald-700"
                >
                  <Link className="h-4 w-4 mr-2" />
                  Lier la transaction et l'├®criture
                </Button>
              </motion.div>
            )}

            {/* R├®sum├® de la s├®lection */}
            {selectedManualTransaction && selectedManualEntry && (
              <Card className="bg-gradient-to-r from-green-50 to-emerald-50 dark:from-green-900/20 dark:to-emerald-900/20 border-green-200 dark:border-green-800">
                <CardContent className="p-4">
                  <div className="flex items-center justify-between">
                    <div className="flex items-center gap-4">
                      <div className="text-center">
                        <p className="text-xs text-gray-500 mb-1">Transaction</p>
                        <p className="font-medium">
                          {bankTransactions.find(t => t.id === selectedManualTransaction)?.description.substring(0, 25)}...
                        </p>
                        <p className="font-bold text-blue-600">
                          {formatAmount(bankTransactions.find(t => t.id === selectedManualTransaction)?.amount || 0)}
                        </p>
                      </div>
                      <div className="flex items-center">
                        <div className="w-8 h-0.5 bg-green-400"></div>
                        <Link className="h-6 w-6 mx-2 text-green-600" />
                        <div className="w-8 h-0.5 bg-green-400"></div>
                      </div>
                      <div className="text-center">
                        <p className="text-xs text-gray-500 mb-1">├ëcriture</p>
                        <p className="font-medium">
                          {accountingEntries.find(e => e.id === selectedManualEntry)?.description.substring(0, 25)}...
                        </p>
                        <p className="font-bold text-purple-600">
                          {formatAmount(accountingEntries.find(e => e.id === selectedManualEntry)?.amount || 0)}
                        </p>
                      </div>
                    </div>
                    {(() => {
                      const tx = bankTransactions.find(t => t.id === selectedManualTransaction);
                      const entry = accountingEntries.find(e => e.id === selectedManualEntry);
                      const diff = tx && entry ? Math.abs(tx.amount - entry.amount) : 0;
                      return diff > 0.01 ? (
                        <Badge variant="destructive">
                          ├ëcart: {formatAmount(diff)}
                        </Badge>
                      ) : (
                        <Badge className="bg-green-600">
                          <CheckCircle className="h-3 w-3 mr-1" />
                          Montants identiques
                        </Badge>
                      );
                    })()}
                  </div>
                </CardContent>
              </Card>
            )}
          </motion.div>
        </TabsContent>
      </Tabs>
    </div>
  );
};
export default BankReconciliation;

// Formulaire de cr├®ation et ├®dition de budget pour CassKai
// Interface professionnelle avec saisie mensuelle et validation
import React, { useState, useEffect } from 'react';
import { Save, X, Plus, Trash2, Calculator, TrendingUp, AlertTriangle, CheckCircle } from 'lucide-react';
import { budgetService } from '@/services/budgetService';
import { AccountSelectDropdown, type AccountOption } from '@/components/budget/AccountSelectDropdown';
import { logger } from '@/lib/logger';
import type {
  BudgetFormData,
  BudgetCategoryFormData,
  BudgetAssumptionFormData,
  BudgetValidationResult
} from '@/types/budget.types';
interface BudgetFormProps {
  companyId: string;
  budgetId?: string; // Pour l'├®dition
  onSave: (budget: any) => void;
  onCancel: () => void;
}
export const BudgetForm: React.FC<BudgetFormProps> = ({
  companyId,
  budgetId,
  onSave,
  onCancel
}) => {
  const [formData, setFormData] = useState<BudgetFormData>({
    year: new Date().getFullYear() + 1,
    categories: [],
    assumptions: []
  });
  const [activeTab, setActiveTab] = useState<'categories' | 'assumptions' | 'summary'>('categories');
  const [validation, setValidation] = useState<BudgetValidationResult | null>(null);
  const [loading, setLoading] = useState(false);
  const [saving, setSaving] = useState(false);
  const monthNames = [
    'Jan', 'F├®v', 'Mar', 'Avr', 'Mai', 'Jun',
    'Jul', 'Ao├╗', 'Sep', 'Oct', 'Nov', 'D├®c'
  ];
  useEffect(() => {
    if (budgetId) {
      loadExistingBudget();
    } else {
      initializeNewBudget();
    }
  }, [budgetId]);
  const loadExistingBudget = async () => {
    try {
      setLoading(true);
      const { data, error } = await budgetService.getBudgetById(budgetId!);
      if (error || !data) {
        logger.error('BudgetForm', 'Error loading budget:', error);
        return;
      }
      setFormData({
        year: data.year,
        categories: data.budget_categories?.map(cat => ({
          account_id: cat.account_id, // Lien vers chart_of_accounts
          category: cat.category,
          subcategory: cat.subcategory,
          category_type: cat.category_type,
          account_codes: cat.account_codes || [],
          annual_amount: cat.annual_amount,
          monthly_amounts: cat.monthly_amounts,
          growth_rate: cat.growth_rate || 0,
          driver_type: cat.driver_type,
          base_value: cat.base_value,
          variable_rate: cat.variable_rate,
          formula: cat.formula,
          notes: cat.notes,
          responsible_person: cat.responsible_person
        })) || [],
        assumptions: data.budget_assumptions?.map(ass => ({
          key: ass.key,
          description: ass.description,
          value: ass.value,
          unit: ass.unit,
          category: ass.category,
          impact_description: ass.impact_description,
          confidence_level: ass.confidence_level,
          source: ass.source
        })) || []
      });
    } catch (error) {
      logger.error('BudgetForm', 'Error loading budget:', error instanceof Error ? error.message : String(error));
    } finally {
      setLoading(false);
    }
  };
  const initializeNewBudget = () => {
    // Budget vide - l'utilisateur devra s├®lectionner des comptes via le dropdown
    const defaultAssumptions: BudgetAssumptionFormData[] = [
      {
        key: 'croissance_marche',
        description: 'Taux de croissance du march├®',
        value: 5,
        unit: '%',
        category: 'market',
        confidence_level: 75,
        source: '├ëtude sectorielle'
      },
      {
        key: 'inflation',
        description: 'Taux d\'inflation pr├®vu',
        value: 2.5,
        unit: '%',
        category: 'economic',
        confidence_level: 80,
        source: 'BCE/INSEE'
      }
    ];
    setFormData({
      year: new Date().getFullYear() + 1,
      categories: [], // Vide - utilisateur doit ajouter via "Ajouter une cat├®gorie"
      assumptions: defaultAssumptions
    });
  };
  const addCategory = () => {
    const newCategory: BudgetCategoryFormData = {
      account_id: '', // Required - will be set via dropdown
      category: '',
      subcategory: '',
      category_type: 'expense',
      account_codes: [],
      annual_amount: 0,
      monthly_amounts: Array(12).fill(0),
      growth_rate: 0,
      driver_type: 'fixed',
      notes: '',
      responsible_person: ''
    };
    setFormData({
      ...formData,
      categories: [...formData.categories, newCategory]
    });
  };
  // Callback pour AccountSelectDropdown - auto-remplit category/subcategory depuis le compte
  const handleAccountSelect = (categoryIndex: number, accountId: string, accountData: AccountOption) => {
    const updatedCategories = [...formData.categories];
    // Mettre ├á jour account_id
    updatedCategories[categoryIndex].account_id = accountId;
    // Auto-remplir category depuis budget_category du compte
    if (accountData.budget_category) {
      updatedCategories[categoryIndex].category = accountData.budget_category;
    }
    // Auto-remplir subcategory depuis account_name du compte
    updatedCategories[categoryIndex].subcategory = accountData.account_name;
    // Auto-remplir category_type depuis account_class
    if (accountData.account_class === 7) {
      updatedCategories[categoryIndex].category_type = 'revenue';
    } else if (accountData.account_class === 6) {
      updatedCategories[categoryIndex].category_type = 'expense';
    } else if (accountData.account_class === 2) {
      updatedCategories[categoryIndex].category_type = 'capex';
    }
    // Auto-remplir account_codes
    updatedCategories[categoryIndex].account_codes = [accountData.account_number];
    setFormData({ ...formData, categories: updatedCategories });
  };
  const removeCategory = (index: number) => {
    setFormData({
      ...formData,
      categories: formData.categories.filter((_, i) => i !== index)
    });
  };
  const updateCategory = (index: number, field: keyof BudgetCategoryFormData, value: any) => {
    const updatedCategories = [...formData.categories];
    if (field === 'monthly_amounts') {
      updatedCategories[index] = { ...updatedCategories[index], [field]: value };
      // Recalculer le montant annuel
      const annualAmount = value.reduce((sum: number, amount: number) => sum + amount, 0);
      updatedCategories[index].annual_amount = annualAmount;
    } else if (field === 'annual_amount') {
      updatedCategories[index] = { ...updatedCategories[index], [field]: value };
      // Redistribuer ├®quitablement sur les 12 mois
      const monthlyAmount = value / 12;
      updatedCategories[index].monthly_amounts = Array(12).fill(monthlyAmount);
    } else {
      updatedCategories[index] = { ...updatedCategories[index], [field]: value };
    }
    setFormData({ ...formData, categories: updatedCategories });
  };
  const updateMonthlyAmount = (categoryIndex: number, monthIndex: number, value: number) => {
    const updatedCategories = [...formData.categories];
    updatedCategories[categoryIndex].monthly_amounts[monthIndex] = value;
    // Recalculer le montant annuel
    const annualAmount = updatedCategories[categoryIndex].monthly_amounts.reduce((sum, amount) => sum + amount, 0);
    updatedCategories[categoryIndex].annual_amount = annualAmount;
    setFormData({ ...formData, categories: updatedCategories });
  };
  const distributeEqually = (categoryIndex: number) => {
    const category = formData.categories[categoryIndex];
    const monthlyAmount = category.annual_amount / 12;
    updateCategory(categoryIndex, 'monthly_amounts', Array(12).fill(monthlyAmount));
  };
  const addAssumption = () => {
    const newAssumption: BudgetAssumptionFormData = {
      key: '',
      description: '',
      value: 0,
      unit: '',
      category: '',
      confidence_level: 80,
      source: ''
    };
    setFormData({
      ...formData,
      assumptions: [...formData.assumptions, newAssumption]
    });
  };
  const removeAssumption = (index: number) => {
    setFormData({
      ...formData,
      assumptions: formData.assumptions.filter((_, i) => i !== index)
    });
  };
  const updateAssumption = (index: number, field: keyof BudgetAssumptionFormData, value: any) => {
    const updatedAssumptions = [...formData.assumptions];
    updatedAssumptions[index] = { ...updatedAssumptions[index], [field]: value };
    setFormData({ ...formData, assumptions: updatedAssumptions });
  };
  const validateForm = (): BudgetValidationResult => {
    const errors: any[] = [];
    const warnings: any[] = [];
    // Validation de l'ann├®e
    const currentYear = new Date().getFullYear();
    if (formData.year < currentYear - 1 || formData.year > currentYear + 5) {
      errors.push({
        field: 'year',
        message: `L'ann├®e doit ├¬tre comprise entre ${  currentYear - 1  } et ${  currentYear + 5}`,
        severity: 'error'
      });
    }
    // Validation des cat├®gories
    if (formData.categories.length === 0) {
      errors.push({
        field: 'categories',
        message: 'Au moins une cat├®gorie budg├®taire est requise',
        severity: 'error'
      });
    }
    formData.categories.forEach((cat, index) => {
      // VALIDATION CRITIQUE : account_id est obligatoire
      if (!cat.account_id || cat.account_id.trim() === '') {
        errors.push({
          field: `categories[${index}].account_id`,
          message: `Cat├®gorie ${index + 1}: Vous devez s├®lectionner un compte comptable`,
          severity: 'error'
        });
      }
      if (!cat.category.trim()) {
        errors.push({
          field: `categories[${index}].category`,
          message: 'Le nom de la cat├®gorie est requis',
          severity: 'error'
        });
      }
      if (cat.monthly_amounts.length !== 12) {
        errors.push({
          field: `categories[${index}].monthly_amounts`,
          message: '12 montants mensuels sont requis',
          severity: 'error'
        });
      }
      // V├®rifier la coh├®rence mensuel/annuel
      const monthlyTotal = cat.monthly_amounts.reduce((sum, amount) => sum + amount, 0);
      if (Math.abs(monthlyTotal - cat.annual_amount) > 0.01) {
        warnings.push({
          field: `categories[${index}].amounts`,
          message: 'Incoh├®rence entre total mensuel et montant annuel',
          suggestion: 'V├®rifiez la r├®partition mensuelle'
        });
      }
    });
    return {
      isValid: errors.length === 0,
      errors,
      warnings
    };
  };
  const handleSave = async () => {
    const validationResult = validateForm();
    setValidation(validationResult);
    if (!validationResult.isValid) {
      return;
    }
    try {
      setSaving(true);
      let result;
      if (budgetId) {
        result = await budgetService.updateBudget(budgetId, formData);
      } else {
        result = await budgetService.createBudget(companyId, formData);
      }
      if (result.error) {
        logger.error('BudgetForm', 'Error saving budget:', result.error);
      } else {
        onSave(result.data);
      }
    } catch (error) {
      logger.error('BudgetForm', 'Error saving budget:', error instanceof Error ? error.message : String(error));
    } finally {
      setSaving(false);
    }
  };
  const calculateTotals = () => {
    const revenue = formData.categories
      .filter(cat => cat.category_type === 'revenue')
      .reduce((sum, cat) => sum + cat.annual_amount, 0);
    const expenses = formData.categories
      .filter(cat => cat.category_type === 'expense')
      .reduce((sum, cat) => sum + cat.annual_amount, 0);
    const capex = formData.categories
      .filter(cat => cat.category_type === 'capex')
      .reduce((sum, cat) => sum + cat.annual_amount, 0);
    return { revenue, expenses, capex, profit: revenue - expenses };
  };
  const totals = calculateTotals();
  const formatCurrency = (amount: number): string => {
    return new Intl.NumberFormat('fr-FR', {
      style: 'currency',
      currency: 'EUR',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0
    }).format(amount);
  };
  if (loading) {
    return (
