                                category.category_type === 'expense' ? 'bg-red-100 text-red-800 dark:bg-red-900/30 dark:text-red-300' :
                                'bg-blue-100 text-blue-800 dark:bg-blue-900/30 dark:text-blue-300'
                              }`}>
                                {category.category_type === 'revenue' ? 'Revenus' :
                                 category.category_type === 'expense' ? 'Charges' : 'Investissements'}
                              </span>
                            </td>
                            <td className="px-6 py-4 whitespace-nowrap text-right font-medium text-gray-900 dark:text-gray-100">
                              {formatCurrency(category.annual_amount)}
                            </td>
                            <td className="px-6 py-4 whitespace-nowrap text-right text-gray-500 dark:text-gray-300">
                              {percentage}%
                            </td>
                          </tr>
                        );
                      })}
                    </tbody>
                  </table>
                </div>
              </div>
              {/* Hypoth├¿ses r├®sum├®es */}
              {formData.assumptions.length > 0 && (
                <div className="bg-white dark:bg-gray-800 dark:bg-gray-700/30 border border-gray-200 dark:border-gray-600 dark:border-gray-700 rounded-lg overflow-hidden">
                  <div className="px-6 py-4 bg-gray-50 dark:bg-gray-700/50 border-b border-gray-200 dark:border-gray-600 dark:border-gray-700">
                    <h4 className="font-medium text-gray-900 dark:text-gray-100">
                      Hypoth├¿ses cl├®s
                    </h4>
                  </div>
                  <div className="p-6">
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                      {formData.assumptions.map((assumption, index) => (
                        <div key={index} className="bg-gray-50 dark:bg-gray-700/50 border border-gray-200 dark:border-gray-600 p-4 rounded-lg">
                          <div className="font-medium text-gray-900 dark:text-gray-100 mb-1">
                            {assumption.description}
                          </div>
                          <div className="text-lg font-semibold text-blue-600 dark:text-blue-400">
                            {assumption.value} {assumption.unit}
                          </div>
                          <div className="text-sm text-gray-500 dark:text-gray-300">
                            Confiance: {assumption.confidence_level}%
                          </div>
                        </div>
                      ))}
                    </div>
                  </div>
                </div>
              )}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

// Gestionnaire de budgets principal pour CassKai - Version modernis├®e

import React, { useState, useRef } from 'react';

import { Button } from '@/components/ui/button';

import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogFooter, DialogDescription } from '@/components/ui/dialog';

import { Plus, Upload, Download, AlertCircle, RefreshCw } from 'lucide-react';

import { BudgetStats } from './BudgetStats';

import { BudgetFilters } from './BudgetFilters';

import { BudgetList } from './BudgetList';

import { useBudgetManager } from '@/hooks/useBudgetManager';

import { budgetImportExportService } from '@/services/budgetImportExportService';

import { budgetService } from '@/services/budgetService';

import { useToast } from '@/components/ui/use-toast';

import type { Budget } from '@/types/budget.types';



interface BudgetManagerProps {

  companyId: string;

  onCreateBudget?: () => void;

  onEditBudget?: (budgetId: string) => void;

  onViewForecast?: (budgetId: string, year: number) => void;

}



export const BudgetManager: React.FC<BudgetManagerProps> = ({

  companyId,

  onCreateBudget,

  onEditBudget,

  onViewForecast

}) => {

  const [searchTerm, setSearchTerm] = useState('');

  const [showCreateModal, setShowCreateModal] = useState(false);

  const [showDeleteDialog, setShowDeleteDialog] = useState(false);

  const [showImportDialog, setShowImportDialog] = useState(false);

  const [budgetToDelete, setBudgetToDelete] = useState<Budget | null>(null);

  const [importing, setImporting] = useState(false);

  const fileInputRef = useRef<HTMLInputElement>(null);

  const { toast } = useToast();



  const {

    budgets,

    loading,

    filter,

    setFilter,

    loadBudgets,

    handleStatusChange,

    handleDuplicate,

    handleDelete

  } = useBudgetManager(companyId);



  const handleDeleteClick = (budget: Budget) => {

    setBudgetToDelete(budget);

    setShowDeleteDialog(true);

  };



  const handleDeleteConfirm = async () => {

    if (!budgetToDelete) return;



    await handleDelete(budgetToDelete.id);

    setShowDeleteDialog(false);

    setBudgetToDelete(null);

  };



  const handleEdit = (budget: Budget) => {

    if (onEditBudget) {

      onEditBudget(budget.id);

    }

  };



  const handleCreateNewBudget = () => {

    if (onCreateBudget) {

      onCreateBudget();

    } else {

      setShowCreateModal(true);

    }

  };



  const handleResetFilters = () => {

    setFilter({});

    setSearchTerm('');

  };



  // T├®l├®charger le mod├¿le Excel

  const handleDownloadTemplate = async () => {

    try {

      const currentYear = new Date().getFullYear();

      await budgetImportExportService.downloadBudgetTemplate(currentYear, 'MonEntreprise');

      toast({

        title: 'Mod├¿le t├®l├®charg├®',

        description: 'Le mod├¿le de budget a ├®t├® t├®l├®charg├® avec succ├¿s',

      });

    } catch (error) {

      toast({

        title: 'Erreur',

        description: error instanceof Error ? error.message : 'Erreur lors du t├®l├®chargement du mod├¿le',

        variant: 'destructive',

      });

    }

  };



  // Importer un fichier Excel

  const handleImportFile = async (event: React.ChangeEvent<HTMLInputElement>) => {

    const file = event.target.files?.[0];

    if (!file) return;



    setImporting(true);

    try {

      // Importer les donn├®es du fichier

      const importData = await budgetImportExportService.importBudgetFromExcel(file);



      // Cr├®er le budget

      const { data: _data, error } = await budgetService.createBudgetFromImport(companyId, importData);



      if (error) throw error;



      toast({

        title: 'Budget import├®',

        description: `Le budget ${importData.year} a ├®t├® import├® avec succ├¿s`,

      });



      // Recharger la liste des budgets

      await loadBudgets();

      setShowImportDialog(false);



    } catch (error) {

      toast({

        title: 'Erreur d\'import',

        description: error instanceof Error ? error.message : 'Erreur lors de l\'import du budget',

        variant: 'destructive',

      });

    } finally {

      setImporting(false);

      if (fileInputRef.current) {

        fileInputRef.current.value = '';

      }

    }

  };



  // Ouvrir la bo├«te de dialogue d'import

  const handleOpenImportDialog = () => {

    setShowImportDialog(true);

  };



  // Exporter un budget vers Excel

  const handleExportBudget = async (budget: Budget) => {

    try {

      await budgetImportExportService.exportBudgetToExcel(

        budget.year,

        budget.budget_categories?.map(cat => ({

          category: cat.category,

          subcategory: cat.subcategory,

          category_type: cat.category_type,

          monthly_amounts: cat.monthly_amounts,

          notes: cat.notes,

        })) || [],

        budget.budget_assumptions?.map(ass => ({

          key: ass.key,

          description: ass.description,

          value: ass.value,

          unit: ass.unit,

          category: ass.category,

        })),

        'MonEntreprise'

      );



      toast({

        title: 'Budget export├®',

        description: `Le budget ${budget.year} a ├®t├® export├® avec succ├¿s`,

      });

    } catch (error) {

      toast({

        title: 'Erreur d\'export',

        description: error instanceof Error ? error.message : 'Erreur lors de l\'export du budget',

        variant: 'destructive',

      });

    }

  };



  // Filtrage local complet avec recherche ET filtres

  const filteredBudgets = budgets.filter(budget => {

    // Filtre par recherche

    if (searchTerm) {

      const searchLower = searchTerm.toLowerCase();

      if (!budget.year.toString().includes(searchLower)) {

        return false;

      }

    }



    // Filtre par ann├®e

    if (filter.years && filter.years.length > 0) {

      if (!filter.years.includes(budget.year)) {

        return false;

      }

    }



    // Filtre par statut

    if (filter.status && filter.status.length > 0) {

      if (!filter.status.includes(budget.status)) {

        return false;

      }

    }



    return true;

  });



  if (loading) {

    return (

      <div className="flex items-center justify-center h-64">

        <div className="flex flex-col items-center space-y-4">

          <RefreshCw className="w-12 h-12 text-blue-600 dark:text-blue-400 animate-spin" />

          <p className="text-gray-600 dark:text-gray-300">Chargement des budgets...</p>

        </div>

      </div>

    );

  }



  return (

    <div className="space-y-6">

      {/* Header */}

      <div className="flex items-center justify-between">

        <div>

          <h1 className="text-2xl font-bold text-gray-900 dark:text-gray-100">

            Gestion des Budgets

          </h1>

          <p className="text-gray-600 dark:text-gray-400 dark:text-gray-300 mt-1">

            Cr├®ez et g├®rez vos budgets annuels avec suivi en temps r├®el

          </p>

        </div>



        <div className="flex space-x-3">

          <Button variant="outline" onClick={loadBudgets}>

            <RefreshCw className="w-4 h-4 mr-2" />

            Actualiser

          </Button>



          <Button variant="outline" onClick={handleOpenImportDialog}>

            <Upload className="w-4 h-4 mr-2" />

            Importer

          </Button>



          <Button onClick={handleCreateNewBudget}>

            <Plus className="w-4 h-4 mr-2" />

            Nouveau Budget

          </Button>

        </div>

      </div>



      {/* Statistics */}

      {budgets.length > 0 && <BudgetStats budgets={budgets} />}



      {/* Filters */}

      <BudgetFilters

        searchTerm={searchTerm}

        setSearchTerm={setSearchTerm}

        filter={filter}

        setFilter={setFilter}

        onResetFilters={handleResetFilters}

        loading={loading}

      />



      {/* Budget List */}

      <BudgetList

        budgets={filteredBudgets}

        onEdit={handleEdit}

        onDuplicate={handleDuplicate}

        onDelete={handleDeleteClick}

        onActivate={(budget) => handleStatusChange(budget.id, 'active')}

        onSubmitForReview={(budget) => handleStatusChange(budget.id, 'under_review')}

        onViewForecast={onViewForecast || (() => {})}

        onCreateBudget={handleCreateNewBudget}

        onExport={handleExportBudget}

      />



      {/* Create Budget Modal */}

      <Dialog open={showCreateModal} onOpenChange={setShowCreateModal}>

        <DialogContent className="max-w-md bg-white dark:bg-gray-800 border-gray-200 dark:border-gray-600 dark:border-gray-700">

          <DialogHeader>

            <DialogTitle className="text-gray-900 dark:text-gray-100">Cr├®er un nouveau budget</DialogTitle>

            <DialogDescription className="text-gray-600 dark:text-gray-300">

              Choisissez comment cr├®er votre budget

            </DialogDescription>

          </DialogHeader>



          <div className="space-y-3">

            <Button

              variant="outline"

              className="w-full justify-start text-left h-auto py-4 dark:bg-gray-700 dark:border-gray-600 dark:hover:bg-gray-600"

              onClick={() => {

                setShowCreateModal(false);

                handleCreateNewBudget();

              }}

            >

              <div>

                <div className="font-medium text-gray-900 dark:text-gray-100">Partir de z├®ro</div>

                <div className="text-sm text-gray-500 dark:text-gray-300">Cr├®er un budget vierge</div>

              </div>

            </Button>



            <Button

              variant="outline"

              className="w-full justify-start text-left h-auto py-4 dark:bg-gray-700 dark:border-gray-600 dark:hover:bg-gray-600"

            >

              <div>

                <div className="font-medium text-gray-900 dark:text-gray-100">Utiliser un mod├¿le</div>

                <div className="text-sm text-gray-500 dark:text-gray-300">Bas├® sur des templates sectoriels</div>

              </div>

            </Button>



            <Button

              variant="outline"

              className="w-full justify-start text-left h-auto py-4 dark:bg-gray-700 dark:border-gray-600 dark:hover:bg-gray-600"

            >

              <div>

                <div className="font-medium text-gray-900 dark:text-gray-100">Dupliquer budget existant</div>

                <div className="text-sm text-gray-500 dark:text-gray-300">Reprendre un budget pr├®c├®dent</div>

              </div>

            </Button>

          </div>



          <DialogFooter>

            <Button variant="outline" onClick={() => setShowCreateModal(false)}>

              Annuler

            </Button>

          </DialogFooter>

        </DialogContent>

      </Dialog>



      {/* Delete Confirmation Dialog */}

      <Dialog open={showDeleteDialog} onOpenChange={setShowDeleteDialog}>

        <DialogContent className="bg-white dark:bg-gray-800 border-gray-200 dark:border-gray-600 dark:border-gray-700">

          <DialogHeader>

            <DialogTitle className="flex items-center space-x-2 text-gray-900 dark:text-gray-100">

              <AlertCircle className="w-5 h-5 text-red-500 dark:text-red-400" />

              <span>Confirmer la suppression</span>

            </DialogTitle>

            <DialogDescription className="text-gray-600 dark:text-gray-300">

              ├ètes-vous s├╗r de vouloir supprimer le budget {budgetToDelete?.year} ?

              Cette action est irr├®versible.

            </DialogDescription>

          </DialogHeader>



          <DialogFooter>

            <Button variant="outline" onClick={() => setShowDeleteDialog(false)}>

              Annuler

            </Button>

            <Button variant="destructive" onClick={handleDeleteConfirm}>

              Supprimer

            </Button>

          </DialogFooter>

        </DialogContent>

      </Dialog>



      {/* Import Budget Dialog */}

      <Dialog open={showImportDialog} onOpenChange={setShowImportDialog}>

        <DialogContent className="max-w-md bg-white dark:bg-gray-800 border-gray-200 dark:border-gray-600 dark:border-gray-700">

          <DialogHeader>

            <DialogTitle className="text-gray-900 dark:text-gray-100">Importer un budget</DialogTitle>

            <DialogDescription className="text-gray-600 dark:text-gray-300">

              Importez un budget depuis un fichier Excel

            </DialogDescription>

          </DialogHeader>



          <div className="space-y-4">

            <div className="border-2 border-dashed border-gray-300 dark:border-gray-600 rounded-lg p-6 text-center">

              <Upload className="w-12 h-12 mx-auto text-gray-400 dark:text-gray-300 mb-3" />

              <p className="text-sm text-gray-600 dark:text-gray-400 dark:text-gray-300 mb-2">

                Glissez-d├®posez votre fichier Excel ou

              </p>

              <input aria-label="Input" placeholder=""

                ref={fileInputRef}

                type="file"

                accept=".xlsx,.xls"

                onChange={handleImportFile}

                disabled={importing}

                className="hidden"

                id="budget-import-file"

              >

              <label htmlFor="budget-import-file">

                <Button

                  variant="outline"

                  disabled={importing}

                  onClick={() => fileInputRef.current?.click()}

                  className="dark:bg-gray-700 dark:border-gray-600"

                  type="button"

                >

                  {importing ? (

                    <>

                      <RefreshCw className="w-4 h-4 mr-2 animate-spin" />

                      Import en cours...

                    </>

                  ) : (

                    <>

                      <Upload className="w-4 h-4 mr-2" />

                      Choisir un fichier

                    </>

                  )}

                </Button>

              </label>

            </div>



            <div className="bg-blue-50 dark:bg-blue-900/20 border border-blue-200 dark:border-blue-800 rounded-lg p-4">

              <p className="text-sm text-blue-900 dark:text-blue-200 font-medium mb-2">

                ­ƒÆí Besoin d'un mod├¿le ?

              </p>

              <p className="text-sm text-blue-700 dark:text-blue-300 mb-3">

                T├®l├®chargez notre mod├¿le Excel pr├®-format├® avec des exemples

              </p>

              <Button

                variant="outline"

                size="sm"

                onClick={handleDownloadTemplate}

                className="w-full dark:bg-gray-700 dark:border-gray-600"

              >

                <Download className="w-4 h-4 mr-2" />

                T├®l├®charger le mod├¿le

              </Button>

            </div>

          </div>



          <DialogFooter>

            <Button variant="outline" onClick={() => setShowImportDialog(false)} disabled={importing}>

              Annuler

            </Button>

          </DialogFooter>

        </DialogContent>

      </Dialog>

    </div>

  );

};

import React, { useState } from 'react';
import { X, DollarSign, Upload } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Textarea } from '@/components/ui/textarea';
import { Expense, Employee } from '@/services/hrService';
import { logger } from '@/lib/logger';
interface ExpenseFormModalProps {
  isOpen: boolean;
  onClose: () => void;
  onSubmit: (data: Omit<Expense, 'id' | 'company_id' | 'created_at' | 'updated_at' | 'employee_name'>) => Promise<boolean>;
  employees: Employee[];
  expense?: Expense | null;
}
export const ExpenseFormModal: React.FC<ExpenseFormModalProps> = ({
  isOpen,
  onClose,
  onSubmit,
  employees,
  expense
}) => {
  const [formData, setFormData] = useState({
    employee_id: expense?.employee_id || '',
    category: expense?.category || 'other' as const,
    description: expense?.description || '',
    amount: expense?.amount?.toString() || '',
    currency: expense?.currency || 'EUR',
    expense_date: expense?.expense_date || new Date().toISOString().split('T')[0],
    notes: expense?.notes || '',
  });
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [errors, setErrors] = useState<Record<string, string>>({});
  const validate = () => {
    const newErrors: Record<string, string> = {};
    if (!formData.employee_id) newErrors.employee_id = 'Employ├® requis';
    if (!formData.description.trim()) newErrors.description = 'Description requise';
    if (!formData.amount || parseFloat(formData.amount) <= 0) {
      newErrors.amount = 'Montant invalide';
    }
    if (!formData.expense_date) newErrors.expense_date = 'Date requise';
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };
  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!validate()) return;
    setIsSubmitting(true);
    try {
      const success = await onSubmit({
        ...formData,
        amount: parseFloat(formData.amount),
        status: 'pending',
        category: formData.category as any,
      });
      if (success) {
        onClose();
      }
    } catch (error) {
      logger.error('ExpenseFormModal', 'Error submitting expense form:', error);
    } finally {
      setIsSubmitting(false);
    }
  };
  if (!isOpen) return null;
  const expenseCategories = {
    travel: 'D├®placement',
    meals: 'Repas',
    transport: 'Transport',
    supplies: 'Fournitures',
    training: 'Formation',
    other: 'Autre'
  };
  return (
    <div
      className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4"
      onClick={onClose}
    >
      <div
        className="bg-white dark:bg-gray-800 rounded-lg shadow-xl max-w-2xl w-full max-h-[90vh] overflow-y-auto"
        onClick={e => e.stopPropagation()}
      >
        {/* Header */}
        <div className="sticky top-0 bg-white dark:bg-gray-800 border-b px-6 py-4 flex items-center justify-between">
          <div className="flex items-center gap-3">
            <DollarSign className="h-6 w-6 text-green-600" />
            <h2 className="text-2xl font-bold text-gray-900 dark:text-gray-100">
              {expense ? 'Modifier la note de frais' : 'Nouvelle Note de Frais'}
            </h2>
          </div>
          <button
            onClick={onClose}
            className="text-gray-400 dark:text-gray-500 hover:text-gray-600 transition-colors dark:text-gray-300"
          >
            <X className="h-6 w-6" />
          </button>
        </div>
        {/* Form */}
        <form onSubmit={handleSubmit} className="p-6">
          <div className="space-y-6">
            {/* Employ├® */}
            <div>
              <Label htmlFor="employee_id">Employ├® *</Label>
              <Select
                value={formData.employee_id}
                onValueChange={value => setFormData({ ...formData, employee_id: value })}
              >
                <SelectTrigger className={errors.employee_id ? 'border-red-500' : ''}>
                  <SelectValue placeholder="S├®lectionner un employ├®" />
                </SelectTrigger>
                <SelectContent>
                  {employees.map(emp => (
                    <SelectItem key={emp.id} value={emp.id}>
                      {emp.first_name} {emp.last_name} - {emp.position}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
              {errors.employee_id && (
                <p className="text-sm text-red-500 mt-1">{errors.employee_id}</p>
              )}
            </div>
            {/* Cat├®gorie et Date */}
            <div className="grid grid-cols-2 gap-4">
              <div>
                <Label htmlFor="category">Cat├®gorie *</Label>
                <Select
                  value={formData.category}
                  onValueChange={value => setFormData({ ...formData, category: value as any })}
                >
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    {Object.entries(expenseCategories).map(([value, label]) => (
                      <SelectItem key={value} value={value}>
                        {label}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>
              <div>
                <Label htmlFor="expense_date">Date *</Label>
                <Input
                  id="expense_date"
                  type="date"
                  value={formData.expense_date}
                  onChange={e => setFormData({ ...formData, expense_date: e.target.value })}
                  className={errors.expense_date ? 'border-red-500' : ''}
                />
                {errors.expense_date && (
                  <p className="text-sm text-red-500 mt-1">{errors.expense_date}</p>
                )}
              </div>
            </div>
            {/* Description */}
            <div>
              <Label htmlFor="description">Description *</Label>
              <Textarea
                id="description"
                value={formData.description}
                onChange={e => setFormData({ ...formData, description: e.target.value })}
                placeholder="D├®crivez la d├®pense..."
                rows={3}
                className={errors.description ? 'border-red-500' : ''}
              />
              {errors.description && (
                <p className="text-sm text-red-500 mt-1">{errors.description}</p>
              )}
            </div>
            {/* Montant et Devise */}
            <div className="grid grid-cols-3 gap-4">
              <div className="col-span-2">
                <Label htmlFor="amount">Montant *</Label>
                <Input
                  id="amount"
                  type="number"
                  step="0.01"
                  value={formData.amount}
                  onChange={e => setFormData({ ...formData, amount: e.target.value })}
                  placeholder="0.00"
                  className={errors.amount ? 'border-red-500' : ''}
                />
                {errors.amount && (
                  <p className="text-sm text-red-500 mt-1">{errors.amount}</p>
                )}
              </div>
              <div>
                <Label htmlFor="currency">Devise</Label>
                <Select
                  value={formData.currency}
                  onValueChange={value => setFormData({ ...formData, currency: value })}
                >
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="EUR">EUR (Ôé¼) - Euro</SelectItem>
                    <SelectItem value="USD">USD ($) - Dollar US</SelectItem>
                    <SelectItem value="GBP">GBP (┬ú) - Livre Sterling</SelectItem>
                    <SelectItem value="CHF">CHF (Fr) - Franc Suisse</SelectItem>
                    <SelectItem value="XOF">XOF (CFA) - Franc CFA BCEAO</SelectItem>
