import { createClient } from '@supabase/supabase-js';

import { logger } from '@/lib/logger';

const supabaseUrl = import.meta.env.VITE_SUPABASE_URL;

const supabaseAnonKey = import.meta.env.VITE_SUPABASE_ANON_KEY;

if (!supabaseUrl) {

  throw new Error('Missing environment variable: VITE_SUPABASE_URL');

}



if (!supabaseAnonKey) {

  throw new Error('Missing environment variable: VITE_SUPABASE_ANON_KEY');

}



// Create Supabase client with proper configuration

// Custom fetch wrapper to rewrite problematic REST selects coming from cached/stale code
// - replace "contacts(... )" with the correct embed "client:customers!customer_id(...)"
// - disambiguate "journals(...)" to the FK relation to avoid PGRST201
const customFetch = async (input: RequestInfo, init?: RequestInit) => {
  try {
    const original = typeof input === 'string' ? input : input instanceof Request ? input.url : String(input);
    let url = original;
    try {
      const u = new URL(url);
      const sel = u.searchParams.get('select');
      if (sel) {
        const decoded = decodeURIComponent(sel);
        let replaced = decoded;
        if (u.pathname.includes('/rest/v1/invoices') && decoded.includes('contacts(')) {
          replaced = replaced.replace(/contacts\(/g, 'client:customers!customer_id(');
        }
        if (u.pathname.includes('/rest/v1/journal_entries') && decoded.includes('journals(')) {
          replaced = replaced.replace(/journals\(/g, 'journals!journal_entries_journal_id_fkey(');
        }
        if (replaced !== decoded) {
          u.searchParams.set('select', replaced);
          url = u.toString();
          if (typeof input === 'string') {
            input = url;
          } else if (input instanceof Request) {
            // Clone init from original Request
            const req = input as Request;
            const newInit: RequestInit = {
              method: req.method,
              headers: req.headers,
              body: init?.body ?? undefined,
              mode: req.mode,
              credentials: req.credentials,
              cache: req.cache,
              redirect: req.redirect,
              referrer: req.referrer,
              integrity: req.integrity,
              keepalive: (req as any).keepalive,
            };
            input = new Request(url, newInit);
          }
        }
      }
    } catch (e) {
      // ignore URL parsing errors and fall back to original
      url = original;
    }
    // Use global fetch implementation
    // @ts-ignore
    return await (globalThis.fetch as typeof fetch)(input, init);
  } catch (err) {
    // Fallback to default fetch on error
    // @ts-ignore
    return await (globalThis.fetch as typeof fetch)(input, init);
  }
};

export const supabase = createClient(supabaseUrl, supabaseAnonKey, {
  auth: {
    autoRefreshToken: true,
    persistSession: true,
    detectSessionInUrl: true,
  },
  db: {
    schema: 'public',
  },
  global: {
    headers: {
      'x-application-name': 'CassKai',
      // Force inclusion of Supabase API key to avoid 400 "No API key found"
      apikey: supabaseAnonKey,
    },
  },
  // Attach custom fetch wrapper
  fetch: customFetch,
});

// Export raw values for services needing explicit headers (fallback fetch)
export const SUPABASE_URL = supabaseUrl;
export const SUPABASE_ANON_KEY = supabaseAnonKey;



// Utility function to handle Supabase errors

export const handleSupabaseError = (error: unknown) => {

  logger.error('Supabase', 'Supabase error', error);



  if (error && typeof error === 'object' && 'message' in error && typeof error.message === 'string') {

    if (error.message.includes('JWT')) {

      return 'Session expired. Please log in again.';

    }



    if (error.message.includes('Row Level Security')) {

      return 'Access denied. You don\'t have permission for this operation.';

    }



    return error.message;

  }



  return 'An unexpected error occurred.';

};



// Helper function to get current user's companies

export const getUserCompanies = async (userId?: string) => {

  let resolvedUserId = userId;

  if (!resolvedUserId) {

    const { data: { user } } = await supabase.auth.getUser();

    if (!user) {

      throw new Error('User not authenticated');

    }

    resolvedUserId = user.id;

  }



  const { data, error } = await supabase

    .from('user_companies')

    .select(`

      *,

      companies:company_id (

        id,

        name,

        country,

        default_currency,

        default_locale,

        timezone,

        is_active

      )

    `)

  .eq('user_id', resolvedUserId);



  if (error) {

    // Gestion gracieuse des erreurs RLS/500 - permet l'onboarding

    if (error.message?.includes('500') ||

        error.message?.includes('policy') ||

        error.message?.includes('RLS') ||

        error.code === '42P17') {

      logger.warn('Supabase', 'RLS/Policy error in getUserCompanies - returning empty array for onboarding');

      return [];

    }

    throw new Error(handleSupabaseError(error));

  }



  return data || [];

};



// Helper function to get current user's default company

export const getCurrentCompany = async (userId?: string) => {

  const companies = await getUserCompanies(userId);

  const defaultCompany = companies.find(uc => uc.is_default);

  return defaultCompany?.companies || companies[0]?.companies || null;

};



