      return true; // Emp├¬cher la propagation de l'erreur
    }
    // Pour les autres erreurs, utiliser le handler original
    if (originalErrorHandler) {
      return originalErrorHandler(message, source, lineno, colno, error);
    }
    return false;
  };
  // G├®rer les erreurs non captur├®es des extensions
  const originalUnhandledRejection = window.onunhandledrejection;
  window.onunhandledrejection = function(event) {
    if (event.reason && (
      event.reason.toString().includes('kwift') ||
      event.reason.toString().includes('chrome-extension') ||
      event.reason.toString().includes('Cannot read properties of undefined')
    )) {
      logger.warn('Main', '­ƒöº Extension promise rejection ignored:', event.reason);
      event.preventDefault(); // Emp├¬cher la propagation
      return;
    }
    // Pour les autres erreurs, utiliser le handler original
    if (originalUnhandledRejection) {
      originalUnhandledRejection.call(window, event);
    }
  };
}
