  const calculateFiscalPeriod = (startMonth: number, startDay: number) => {

    const months = [

      'janvier', 'f├®vrier', 'mars', 'avril', 'mai', 'juin',

      'juillet', 'ao├╗t', 'septembre', 'octobre', 'novembre', 'd├®cembre'

    ];



    const normalizedMonth = ((isNaN(startMonth) ? 0 : startMonth) + 12) % 12;

    const normalizedDay = Math.min(Math.max(startDay || 1, 1), 31);

    const endMonth = normalizedMonth;



    if (normalizedDay === 1) {

      const prevMonth = normalizedMonth === 0 ? 11 : normalizedMonth - 1;

      const year = new Date().getFullYear();

      const lastDay = new Date(year, prevMonth + 1, 0).getDate();

      return `Du 1er ${months[normalizedMonth]} au ${lastDay} ${months[prevMonth]} (ann├®e suivante)`;

    }



    const endDay = normalizedDay - 1;

    return `Du ${normalizedDay} ${months[normalizedMonth]} au ${endDay} ${months[endMonth]} (ann├®e suivante)`;

  };



  const normalizedStartMonth = Math.max(1, companyData.fiscalYearStartMonth ?? 1) - 1;

  const startDay = companyData.fiscalYearStartDay ?? 1;

  const fiscalPeriod = calculateFiscalPeriod(normalizedStartMonth, startDay);



