/**
 * Service de r├¿gles comptables
 */
export class AccountingRulesService {
  /**
   * Prefixes for accounts that are commonly flexible (both sides used)
   * Includes bank, cash, key auxiliaries and state accounts.
   */
  private static readonly FLEXIBLE_ACCOUNT_PREFIXES = [
    '512', // Banque
    '53',  // Caisse
    '411', // Clients (auxiliaires)
    '401', // Fournisseurs (auxiliaires)
    '467', // Autres comptes d├®biteurs ou cr├®diteurs
    '44',  // ├ëtat, TVA et assimil├®s (g├®n├®ralement mixtes)
  ];
  /**
   * Permet de surcharger la liste des pr├®fixes ┬½ flexibles ┬╗
   * (optionnel, pour adapter aux pratiques d'une soci├®t├® ou d'un r├®f├®rentiel)
   */
  static setFlexibleAccountPrefixes(prefixes: string[]) {
    if (Array.isArray(prefixes) && prefixes.every(p => typeof p === 'string')) {
      // Cr├®er une copie pour ├®viter les mutations ext├®rieures
      (this as any).FLEXIBLE_ACCOUNT_PREFIXES = [...prefixes];
    }
  }
  /**
   * Retourne la liste courante des pr├®fixes ┬½ flexibles ┬╗
   */
  static getFlexibleAccountPrefixes(): string[] {
    return [...(this as any).FLEXIBLE_ACCOUNT_PREFIXES];
  }
  /**
   * R├®cup├¿re la classe d'un compte depuis son num├®ro
   */
  static getAccountClass(accountNumber: string): AccountClass | null {
    if (!accountNumber || accountNumber.length === 0) return null;
    const firstDigit = parseInt(accountNumber[0]);
    if (isNaN(firstDigit) || firstDigit < 1 || firstDigit > 8) return null;
    return firstDigit as AccountClass;
  }
  /**
   * D├®termine la nature comptable d'un compte
   */
  static getAccountNature(accountNumber: string): AccountNature {
    const accountClass = this.getAccountClass(accountNumber);
    if (!accountClass) return AccountNature.MIXED;
    // Classe 4: traitement sp├®cial (clients vs fournisseurs)
    if (accountClass === AccountClass.THIRD_PARTIES) {
      if (accountNumber.startsWith('411')) return AccountNature.DEBIT;  // Clients (actif)
      if (accountNumber.startsWith('401')) return AccountNature.CREDIT; // Fournisseurs (passif)
      if (accountNumber.startsWith('43')) return AccountNature.CREDIT;  // S├®curit├® sociale
      if (accountNumber.startsWith('44')) return AccountNature.MIXED;   // ├ëtat, TVA
      return AccountNature.MIXED;
    }
    return ACCOUNT_RULES[accountClass]?.nature || AccountNature.MIXED;
  }
  /**
   * Valide si un montant est du bon c├┤t├® pour un compte donn├®
   */
  static validateAccountSide(
    accountNumber: string,
    debitAmount: number,
    creditAmount: number
  ): { valid: boolean; warning?: string; suggestion?: string; info?: string } {
    // Comptes pour lesquels d├®bit et cr├®dit sont r├®guli├¿rement utilis├®s sans avertissement
    const isFlexibleAccount = this.FLEXIBLE_ACCOUNT_PREFIXES.some(prefix => accountNumber.startsWith(prefix));
    const nature = this.getAccountNature(accountNumber);
    const accountClass = this.getAccountClass(accountNumber);
    // Les deux c├┤t├®s remplis = erreur
    if (debitAmount > 0 && creditAmount > 0) {
      return {
        valid: false,
        warning: 'Un compte ne peut pas avoir simultan├®ment un d├®bit ET un cr├®dit',
        suggestion: 'Choisissez soit d├®bit, soit cr├®dit',
      };
    }
    // Aucun montant = valide mais incomplet
    if (debitAmount === 0 && creditAmount === 0) {
      return {
        valid: true,
        warning: 'Aucun montant saisi',
      };
    }
    // Comptes flexibles: accepter sans avertissement c├┤t├® d├®bit/cr├®dit
    if (isFlexibleAccount) {
      return { valid: true };
    }
    // Comptes mixtes : tout est permis
    if (nature === AccountNature.MIXED) {
      return { valid: true };
    }
    // Comptes ├á nature d├®bitrice (2, 3, 5, 6)
    if (nature === AccountNature.DEBIT) {
      if (creditAmount > 0) {
        const rule = accountClass ? ACCOUNT_RULES[accountClass] : null;
        return {
          valid: true,
          info: `Info: Ce compte est g├®n├®ralement d├®bit├® (${rule?.description}).`,
          suggestion: `Ce type de compte (classe ${accountClass}) est habituellement d├®bit├®. V├®rifiez votre saisie.`,
        };
      }
    }
    // Comptes ├á nature cr├®ditrice (1, 7)
    if (nature === AccountNature.CREDIT) {
      if (debitAmount > 0) {
        const rule = accountClass ? ACCOUNT_RULES[accountClass] : null;
        return {
          valid: true,
          info: `Info: Ce compte est g├®n├®ralement cr├®dit├® (${rule?.description}).`,
          suggestion: `Ce type de compte (classe ${accountClass}) est habituellement cr├®dit├®. V├®rifiez votre saisie.`,
        };
      }
    }
    return { valid: true };
  }
  /**
   * G├®n├¿re un num├®ro d'├®criture automatique selon le journal
   * Format: [CODE_JOURNAL]-[ANN├ëE]-[NUM├ëRO_S├ëQUENTIEL]
   * Exemple: VE-2025-00123
   */
  static async generateEntryNumber(
    companyId: string,
    journalId: string,
    entryDate: string
  ): Promise<string> {
    try {
      // R├®cup├®rer le code du journal
      const { data: journal, error: journalError } = await supabase
        .from('journals')
        .select('code')
        .eq('id', journalId)
        .single();
      if (journalError || !journal) {
        logger.warn('AccountingRules', 'Journal not found, using default code');
        const year = new Date(entryDate).getFullYear();
        return `OD-${year}-${Date.now().toString().slice(-6)}`;
      }
      const journalCode = journal.code;
      const year = new Date(entryDate).getFullYear();
      // R├®cup├®rer le dernier num├®ro pour ce journal cette ann├®e
      const { data: lastEntry, error: entryError } = await supabase
        .from('journal_entries')
        .select('entry_number')
        .eq('company_id', companyId)
        .eq('journal_id', journalId)
        .gte('entry_date', `${year}-01-01`)
        .lte('entry_date', `${year}-12-31`)
        .order('entry_number', { ascending: false })
        .limit(1)
        .single();
      let nextNumber = 1;
      if (!entryError && lastEntry && lastEntry.entry_number) {
        // Extraire le num├®ro s├®quentiel du dernier num├®ro
        const match = lastEntry.entry_number.match(/-(\d+)$/);
        if (match) {
          nextNumber = parseInt(match[1]) + 1;
        }
      }
      // Format: VE-2025-00001
      return `${journalCode}-${year}-${nextNumber.toString().padStart(5, '0')}`;
    } catch (error) {
      logger.error('AccountingRules', 'Error generating entry number:', error);
      const year = new Date(entryDate).getFullYear();
      return `OD-${year}-${Date.now().toString().slice(-6)}`;
    }
  }
  /**
   * D├®termine automatiquement le journal appropri├® selon les comptes utilis├®s
   * et le r├®f├®rentiel comptable de l'entreprise.
   *
   * R├êGLES PAR R├ëF├ëRENTIEL :
   *
   * PCG (France) / SCF (Maghreb) / SYSCOHADA (OHADA) :
   * - Classe 6 (charges) ÔåÆ ACHATS
   * - Classe 7 (produits) ÔåÆ VENTES
   * - Classe 8 (HAO - SYSCOHADA uniquement) ÔåÆ OD
   * - Compte 512 (banque) ÔåÆ BANQUE
   * - Compte 53x (caisse) ÔåÆ CAISSE
   * - Sinon ÔåÆ OD (Op├®rations Diverses)
   *
   * IFRS (International / Pays anglophones) :
   * - Classe 6 (Revenue) ÔåÆ VENTES (invers├® par rapport au PCG!)
   * - Classe 7 (Expenses) ÔåÆ ACHATS (invers├® par rapport au PCG!)
   * - Comptes bancaires ÔåÆ BANQUE
   * - Sinon ÔåÆ OD
   *
   * @param accountNumbers Liste des num├®ros de comptes utilis├®s dans l'├®criture
   * @param standard R├®f├®rentiel comptable (PCG, SYSCOHADA, IFRS, SCF). Par d├®faut: PCG
   */
  static suggestJournal(accountNumbers: string[], standard: 'PCG' | 'SYSCOHADA' | 'IFRS' | 'SCF' = 'PCG'): JournalType {
    // D├®tection des comptes sp├®cifiques (communs ├á tous les r├®f├®rentiels)
    const hasBank = accountNumbers.some(acc => acc.startsWith('512') || acc.startsWith('52')); // 512 ou 52x
    const hasCash = accountNumbers.some(acc => acc.startsWith('53') || acc.startsWith('57')); // 53x ou 57x

    // ========================================
    // IFRS - Structure diff├®rente (classes invers├®es)
    // ========================================
    if (standard === 'IFRS') {
      // IFRS: Class 6 = Revenue, Class 7 = Expenses
      const hasRevenueIFRS = accountNumbers.some(acc => acc.startsWith('6'));
      const hasExpenseIFRS = accountNumbers.some(acc => acc.startsWith('7'));
      const hasReceivables = accountNumbers.some(acc => acc.startsWith('11') || acc.startsWith('12')); // Trade receivables
      const hasPayables = accountNumbers.some(acc => acc.startsWith('21') || acc.startsWith('22')); // Trade payables

      // Ventes IFRS: classe 6 (Revenue) ou cr├®ances clients
      if (hasRevenueIFRS && !hasExpenseIFRS) return JournalType.SALE;
      if (hasRevenueIFRS && hasReceivables) return JournalType.SALE;

      // Achats IFRS: classe 7 (Expenses) ou dettes fournisseurs
      if (hasExpenseIFRS && !hasRevenueIFRS) return JournalType.PURCHASE;
      if (hasExpenseIFRS && hasPayables) return JournalType.PURCHASE;

      // Banque
      if (hasBank) return JournalType.BANK;
      if (hasCash) return JournalType.CASH;

      return JournalType.MISCELLANEOUS;
    }

    // ========================================
    // PCG / SCF / SYSCOHADA - Structure classique
    // ========================================
    const hasClient = accountNumbers.some(acc => acc.startsWith('411'));
    const hasSupplier = accountNumbers.some(acc => acc.startsWith('401'));
    const hasSale = accountNumbers.some(acc => acc.startsWith('70')); // 70x = Ventes
    const hasPurchase = accountNumbers.some(acc => acc.startsWith('60')); // 60x = Achats
    const hasExpenseAccount = accountNumbers.some(acc => acc.startsWith('6')); // Classe 6 = Charges
    const hasIncomeAccount = accountNumbers.some(acc => acc.startsWith('7')); // Classe 7 = Produits

    // SYSCOHADA: Classe 8 = HAO (Hors Activit├®s Ordinaires) ÔåÆ toujours OD
    if (standard === 'SYSCOHADA') {
      const hasHAO = accountNumbers.some(acc => acc.startsWith('8'));
      if (hasHAO) {
        // Les comptes HAO vont toujours en OD
        return JournalType.MISCELLANEOUS;
      }
    }

    // R├¿gles de d├®termination automatique (ordre de priorit├®)

    // 1. Ventes : compte 70x (produits) OU client 411
    if (hasSale || (hasIncomeAccount && hasClient)) return JournalType.SALE;
    if (hasIncomeAccount && !hasExpenseAccount) return JournalType.SALE;

    // 2. Achats : compte 60x (charges) OU fournisseur 401
    // CORRECTION┬á: priorit├® absolue ├á l'achat si 60x ou 401 pr├®sent, m├¬me si banque/caisse aussi pr├®sent
    // ÔÜá´©Å CORRECTION: Tester BANQUE/CAISSE AVANT achats
    // Car un paiement fournisseur (607+512) doit aller dans BANQUE
    if (hasBank && (hasPurchase || hasSale)) return JournalType.BANK;
    if (hasCash && (hasPurchase || hasSale)) return JournalType.CASH;
    if (hasBank) return JournalType.BANK;
    if (hasCash) return JournalType.CASH;

    // Maintenant achats (si pas de tr├®sorerie)
    if (hasPurchase || (hasExpenseAccount && hasSupplier)) return JournalType.PURCHASE;
    if (hasExpenseAccount && !hasIncomeAccount) return JournalType.PURCHASE;

    // 4. Par d├®faut : Op├®rations Diverses
    return JournalType.MISCELLANEOUS;
  }

  /**
   * Retourne une description des r├¿gles d'attribution des journaux
   * selon le r├®f├®rentiel comptable
   */
  static getJournalRulesDescription(standard: 'PCG' | 'SYSCOHADA' | 'IFRS' | 'SCF' = 'PCG'): Record<JournalType, string[]> {
    if (standard === 'IFRS') {
      return {
        [JournalType.SALE]: ['Class 6 (Revenue)', 'Trade Receivables (11x, 12x)'],
        [JournalType.PURCHASE]: ['Class 7 (Expenses)', 'Trade Payables (21x, 22x)'],
        [JournalType.BANK]: ['Bank accounts (52x)'],
        [JournalType.CASH]: ['Cash accounts (57x)'],
        [JournalType.MISCELLANEOUS]: ['All other accounts', 'Mixed transactions'],
      };
    }

    // PCG / SCF / SYSCOHADA (structure similaire)
    const rules: Record<JournalType, string[]> = {
      [JournalType.SALE]: ['Classe 7 (Produits)', 'Compte 411 (Clients)'],
      [JournalType.PURCHASE]: ['Classe 6 (Charges)', 'Compte 401 (Fournisseurs)'],
      [JournalType.BANK]: ['Compte 512 (Banque)'],
      [JournalType.CASH]: ['Compte 53x (Caisse)'],
      [JournalType.MISCELLANEOUS]: ['Classes 1-5 (Bilan)', '├ëcritures mixtes'],
    };

    // Ajouter HAO pour SYSCOHADA
    if (standard === 'SYSCOHADA') {
      rules[JournalType.MISCELLANEOUS].push('Classe 8 (HAO - Hors Activit├®s Ordinaires)');
    }

    return rules;
  }
  /**
   * R├®cup├¿re le template d'├®criture pour un type de journal
   */
  static getJournalTemplate(journalType: JournalType) {
    return JOURNAL_ENTRY_TEMPLATES[journalType];
  }
  /**
   * Valide une ├®criture compl├¿te selon les r├¿gles comptables
   */
  static validateJournalEntry(entry: {
    lines: Array<{
      accountNumber: string;
      debitAmount: number;
      creditAmount: number;
    }>;
  }): {
    valid: boolean;
    errors: string[];
    warnings: string[];
  } {
    const errors: string[] = [];
    const warnings: string[] = [];
    // 1. V├®rifier l'├®quilibre d├®bit/cr├®dit
    let totalDebit = 0;
    let totalCredit = 0;
    entry.lines.forEach(line => {
      totalDebit += line.debitAmount;
      totalCredit += line.creditAmount;
    });
    const difference = Math.abs(totalDebit - totalCredit);
    if (difference > 0.01) {
      errors.push(
        `L'├®criture n'est pas ├®quilibr├®e: D├®bit ${totalDebit.toFixed(2)}Ôé¼ Ôëá Cr├®dit ${totalCredit.toFixed(2)}Ôé¼ (diff├®rence: ${difference.toFixed(2)}Ôé¼)`
      );
    }
    // 2. Minimum 2 lignes requises
    if (entry.lines.length < 2) {
      errors.push('Une ├®criture comptable doit comporter au moins 2 lignes');
    }
    // 3. Valider chaque ligne
    entry.lines.forEach((line, index) => {
      const validation = this.validateAccountSide(
        line.accountNumber,
        line.debitAmount,
        line.creditAmount
      );
      if (!validation.valid) {
        errors.push(`Ligne ${index + 1} (${line.accountNumber}): ${validation.warning}`);
      } else if (validation.warning) {
        warnings.push(`Ligne ${index + 1} (${line.accountNumber}): ${validation.warning}`);
      }
    });
    return {
      valid: errors.length === 0,
      errors,
      warnings,
    };
  }
  /**
   * R├®cup├¿re les informations d'un compte
   */
  static async getAccountInfo(companyId: string, accountId: string) {
    try {
      const { data, error } = await supabase
        .from('chart_of_accounts')
        .select('*')
        .eq('company_id', companyId)
        .eq('id', accountId)
        .single();
      if (error) throw error;
      return {
        ...data,
        nature: this.getAccountNature(data.account_number || ''),
        class: this.getAccountClass(data.account_number || ''),
      };
    } catch (error) {
      logger.error('AccountingRules', 'Error fetching account info:', error);
      return null;
    }
  }
}
export default AccountingRulesService;
